<?php if (!defined('FLUX_ROOT')) exit; ?><br/><h2>Edit Voting Site - Administrator</h2><?php if(count($error)>0){ ?><h3>Error:</h3><ul>	<?php foreach($error as $err){ ?>		<li><?php echo $err; ?></li>	<?php } ?></ul><?php } ?><form action="<?php echo $this->url ?>&sid=<?php echo $site_id; ?>&return=edit_site"  method="post" enctype="multipart/form-data" class="generic-form"><table class="vertical-table" width="100%">	<tr>		<th align="left">			<?php echo Flux::message('SITE_NAME_LABEL'); ?>						</th>		<td>			<input type="text" name="site_name" id="id_site_name" value="<?php echo $vp->site_name; ?>" placeholder="Enter Site Name" required="required" />		</td>	</tr>	<tr>		<th align="left">			<?php echo Flux::message('ADDRESS_LABEL'); ?>						</th>		<td>			<input type="text" name="site_address" id="id_site_address"  value="<?php echo $vp->address; ?>" placeholder="Enter Site Name" required="required" />		</td>	</tr>	<tr>		<th align="left">			<?php echo Flux::message('BANNER_LABEL'); ?>				</th>		<td>			<input type="file" name="banner_upload" id="id_banner_upload" value="<?php echo $params->get('banner_upload'); ?>"  <?php if($vp->banner_url!=""){ echo "DISABLED"; } ?>/>			<input type="checkbox" name="cbox_banner_url" id="id_cbox_banner_url"  <?php if($vp->banner_url!=""){ echo "CHECKED"; } ?> /><i>URL Banner:</i>			<input type="text" name="banner_url" id="id_banner_url" value="<?php echo $vp->banner_url; ?>" placeholder="Enter Banner URL" <?php if($vp->banner_url==""){ echo "DISABLED"; } ?> />			<br/>			<?php if($vp->banner_url==""): ?>			<i><?php echo Flux::message('BANNER_IS_UPLOADED'); ?>.			<?php endif; ?>			<i style="color:red">Allowed file types for file upload: <?php echo $file_types; ?></i>		</td>	</tr>	<tr>		<th align="left">			<?php echo Flux::message('BLOCK_TIME_LABEL'); ?>				</th>		<td>			<select name="blocking_hours" id="id_blocking_hours">				<?php for($i = 1; $i<=24; $i++){ ?>				<option value="<?php echo $i; ?>" <?php if($hours==$i){ echo "SELECTED"; } ?>><?php echo $i; ?></option>				<?php } ?>			</select>			<i>hr(s).</i>			<select name="blocking_minutes" id="id_blocking_minutes">				<?php for($i = 0; $i<=59; $i++){ ?>				<option value="<?php echo $i; ?>" <?php if($minutes==$i){ echo "SELECTED"; } ?>><?php echo $i; ?></option>				<?php } ?>			</select>			<i>min(s).</i>		</td>	</tr>	<tr>		<th align="left">			<?php echo Flux::message('POINTS_LABEL'); ?>				</th>		<td>			<input type="text" name="points" id="id_points"  value="<?php echo $vp->points; ?>" placeholder="Enter Points" required="required" /> pt('s)		</td>	</tr>	<tr>		<td colspan="2" align="right">			<input type="reset" value="Clear"/>			<input type="submit" name="Edit" id="Edit" value="Submit"/>		</td>	</tr></table></form><script type="text/javascript" language="javascript">	$('#id_cbox_banner_url').change(function(){		var checked = $(this).attr('CHECKED');			$('#id_banner_url').attr('DISABLED',!checked);			$('#id_banner_upload').attr('DISABLED',!!checked);	});</script>