<?php if (!defined('FLUX_ROOT')) exit; ?><?php if(!empty($vp_sites)): ?><br/><h2>Voting Site List - Administrator</h2><table class="horizontal-table" width="100%">  	<tr>		<th align="center">Name</th>		<th align="center">Banner</th>		<th align="center">Points</th>		<th align="center">Block Time</th>		<th align="center">Controls</th>	</tr><?php foreach($vp_sites as $site): ?>	<tr>		<td><?php echo $site->site_name;?></td>		<td align="center" width="150" height="50"><img src="<?php if($site->banner_url!="") { echo $site->banner_url; } else { echo "index.php?module=voteforpoints&action=image&sid=".$site->site_id; }?>" width="150" height="50"/></td>		<td align="right"><?php echo $site->points;?> pt('s)</td>		<td align="center"><?php echo strBlockTime($site->blocking_time); ?></td>		<td align="center">			<a href="?module=voteforpoints&action=edit&sid=<?php echo $site->site_id; ?>">Edit</a>&nbsp;&nbsp;|&nbsp;&nbsp;			<a href="?module=voteforpoints&action=delete&sid=<?php echo $site->site_id; ?>">Delete</a>		</td>	</tr><?php endforeach; ?></table><?php else: ?><br/><i>No voting sites found</i><?php endif; ?>